function ajax_get_json(){var n=document.getElementById("potions"),e=new XMLHttpRequest;e.open("GET","js/potions.json",!0),e.setRequestHeader("Content-type","application/json",!0),e.onreadystatechange=function(){if(4===e.readyState&&200===e.status){var t=JSON.parse(e.responseText);n.innerHTML="";for(var o in t.potions)n.innerHTML+='<div class="potions-thumb"><img src="img/thumbs/'+t.potions[o].image+'"><p>'+t.potions[o].name+' - <strong class="price">$'+t.potions[o].price+'</strong></p></div><div class="potions-modal"><div class="potions-photo"><img src="img/'+t.potions[o].image+'"></div><h2>'+t.potions[o].name+"</h2><h2>Use/Effect</h2><p>"+t.potions[o].effect+"</p><h2>Ingredients:</h2><ul></ul><h2>Price:</h2><h2>$ "+t.potions[o].price+"</h2></div>"}},e.send(null),n.innerHTML="requesting..."}