function ajax_get_json(){var e=document.getElementById("potions"),n=new XMLHttpRequest;n.open("GET","js/potions.json",!0),n.setRequestHeader("Content-type","application/json",!0),n.onreadystatechange=function(){if(4===n.readyState&&200===n.status){var s=JSON.parse(n.responseText);e.innerHTML="";for(var o in s.potions){var i=s.potions[o],t=i.ingredients;console.log("before nested loop!");for(var a=0;a<t.length;a++){var r="<li>"+t[a]+"</li>";console.log(r)}e.innerHTML+='<div class="potions-thumb"><img src="img/thumbs/'+i.image+'"><p>'+i.name+' - <strong class="price">$'+i.price+'</strong></p></div><div class="potions-modal"><div class="potions-photo"><img src="img/'+i.image+'"></div><div class="potions-infos"><h2>'+i.name+"</h2><h2>Use/Effect</h2><p>"+i.effect+"</p><h2>Ingredients:</h2><ul>"+r+'</ul><h2>Price:</h2><h2 class="price">$ '+i.price+"</h2></div></div>"}}},n.send(null),e.innerHTML="requesting..."}