function ajax_get_json(){var e=document.getElementById("potions"),n=new XMLHttpRequest;n.open("GET","js/potions.json",!0),n.setRequestHeader("Content-type","application/json",!0),n.onreadystatechange=function(){if(4===n.readyState&&200===n.status){var s=JSON.parse(n.responseText);e.innerHTML="";for(var i in s.potions){var o=s.potions[i];console.log("before nested loop!"),console.log(o.ingredients),e.innerHTML+='<div class="potions-thumb"><img src="img/thumbs/'+o.image+'"><p>'+o.name+' - <strong class="price">$'+o.price+'</strong></p></div><div class="potions-modal"><div class="potions-photo"><img src="img/'+o.image+'"></div><div class="potions-infos"><h2>'+o.name+"</h2><h2>Use/Effect</h2><p>"+o.effect+"</p><h2>Ingredients:</h2><ul>"+o.ingredients+'</ul><h2>Price:</h2><h2 class="price">$ '+o.price+"</h2></div></div>"}}},n.send(null),e.innerHTML="requesting..."}