function ajax_get_json(){var e=document.getElementById("potions"),n=new XMLHttpRequest;n.open("GET","js/potions.json",!0),n.setRequestHeader("Content-type","application/json",!0),n.onreadystatechange=function(){if(4===n.readyState&&200===n.status){var s=JSON.parse(n.responseText);e.innerHTML="";for(var o in s.potions){var t=s.potions[o],i=t.ingredients;console.log("before nested loop!");for(var r=0;r<i.length;r++)console.log(i[r]),console.log("nested loop!");e.innerHTML+='<div class="potions-thumb"><img src="img/thumbs/'+t.image+'"><p>'+t.name+' - <strong class="price">$'+t.price+'</strong></p></div><div class="potions-modal"><div class="potions-photo"><img src="img/'+t.image+'"></div><div class="potions-infos"><h2>'+t.name+"</h2><h2>Use/Effect</h2><p>"+t.effect+"</p><h2>Ingredients:</h2><ul>"+t.ingredients+'</ul><h2>Price:</h2><h2 class="price">$ '+t.price+"</h2></div></div>"}}},n.send(null),e.innerHTML="requesting..."}