function ajax_get_json(){var s=document.getElementById("potions"),n=new XMLHttpRequest;n.open("GET","js/potions.json",!0),n.setRequestHeader("Content-type","application/json",!0),n.onreadystatechange=function(){if(4===n.readyState&&200===n.status){var e=JSON.parse(n.responseText);s.innerHTML="";for(var o in e.potions)s.innerHTML+='<div class="potions-thumb"><img src="img/thumbs/'+e.potions[o].image+'"><p>'+e.potions[o].name+' - <strong class="price">$'+e.potions[o].price+'</strong></p></div><div class="potions-modal"><div class="potions-photo"><img src="img/'+e.potions[o].image+'"></div><div class="potions-infos"><h2>'+e.potions[o].name+"</h2><h2>Use/Effect</h2><p>"+e.potions[o].effect+'</p><h2>Ingredients:</h2><ul></ul><h2>Price:</h2><h2 class="price">$ '+e.potions[o].price+"</h2></div></div>"}},n.send(null),s.innerHTML="requesting..."}