function ajax_get_json(){var n=document.getElementById("potions"),s=new XMLHttpRequest;s.open("GET","js/potions.json",!0),s.setRequestHeader("Content-type","application/json",!0),s.onreadystatechange=function(){if(4===s.readyState&&200===s.status){var o=JSON.parse(s.responseText);n.innerHTML="";for(var e in o.potions)n.innerHTML+='<div class="potions-thumb"><img src="img/thumbs/'+o.potions[e].image+'"><p>'+o.potions[e].name+' - <strong class="price">$'+o.potions[e].price+'</strong></p></div><div class="potions-modal"><div class="potions-photo"><img src="img/'+o.potions[e].image+'"></div><div class="potions-infos"><h2>'+o.potions[e].name+"</h2><h2>Use/Effect</h2><p>"+o.potions[e].effect+"</p><h2>Ingredients:</h2><ul></ul><h2>Price:</h2><h2>$ "+o.potions[e].price+"</h2></div></div>"}},s.send(null),n.innerHTML="requesting..."}