function ajax_get_json(){var e=document.getElementById("potions"),n=new XMLHttpRequest;n.open("GET","js/potions.json",!0),n.setRequestHeader("Content-type","application/json",!0),n.onreadystatechange=function(){if(4===n.readyState&&200===n.status){var t=JSON.parse(n.responseText);e.innerHTML="";for(var o in t.potions)e.innerHTML+='<div class="potions-thumb"><img src="img/thumbs/'+t.potions[o].image+'"><p>'+t.potions[o].name+' - <strong class="price">$'+t.potions[o].price+'</strong></p></div><div class="potions-photo"><img src="img/'+t.potions[o].image+'"></div><h2>'+t.potions[o].name+"</h2><h2>Use/Effect</h2><p>"+t.potions[o].effect+"</p><h2>Ingredients:</h2><ul></ul><h2>Price:</h2><h2>$ "+t.potions[o].price+"</h2>"}},n.send(null),e.innerHTML="requesting..."}