function ajax_get_json(){var e=document.getElementById("potions"),n=new XMLHttpRequest;n.open("GET","js/potions.json",!0),n.setRequestHeader("Content-type","application/json",!0),n.onreadystatechange=function(){if(4===n.readyState&&200===n.status){var t=JSON.parse(n.responseText);e.innerHTML="";for(var o in t.potions){var s=t.potions[o],i=s.ingredients;console.log("before nested loop!");for(var a=0;a<i.length;a++){var r="<li>"+i[a]+"</li>";console.log(r)}e.innerHTML+='<div id="thumb" class="potions-thumb"><img src="img/thumbs/'+s.image+'"><p>'+s.name+' - <strong class="price">$'+s.price+'</strong></p></div><div id="modal" class="potions-modal"><div class="potions-photo"><img src="img/'+s.image+'"></div><div class="potions-infos"><h2>'+s.name+"</h2><h2>Use/Effect</h2><p>"+s.effect+"</p><h2>Ingredients:</h2><ul>"+r+'</ul><h2>Price:</h2><h2 class="price">$ '+s.price+"</h2></div></div>"}}},n.send(null),e.innerHTML="requesting..."}function showModal(){var e=document.getElementById("thumb"),n=document.getElementById("modal");e.addEventListener("click",function(e){n.style.display="block"})}