function ajax_get_json(){var e=document.getElementById("potions"),n=new XMLHttpRequest;n.open("GET","js/potions.json",!0),n.setRequestHeader("Content-type","application/json",!0),n.onreadystatechange=function(){if(4===n.readyState&&200===n.status){var t=JSON.parse(n.responseText);e.innerHTML="";for(var s in t.potions)e.innerHTML+='<div class="potions-thumb"><img src="img/thumbs/'+t.potions[s].image+'"></div><h2>'+t.potions[s].name+"</h2><h2>Use/Effect</h2><p>"+t.potions[s].effect+"<p><h2>Ingredients:</h2>"}},n.send(null),e.innerHTML="requesting..."}