function ajax_get_json(){var n=document.getElementById("potions"),e=new XMLHttpRequest;e.open("GET","js/potions.json",!0),e.setRequestHeader("Content-type","application/json",!0),e.onreadystatechange=function(){if(4===e.readyState&&200===e.status){var t=JSON.parse(e.responseText);n.innerHTML="";for(var o in t.potions)n.innerHTML+=t.potions[o].id+t.potions[o].name}},e.send(null),n.innerHTML="requesting..."}