function ajax_get_json(){var e=document.getElementById("potions"),n=new XMLHttpRequest;n.open("GET","js/potions.json",!0),n.setRequestHeader("Content-type","application/json",!0),n.onreadystatechange=function(){if(4===n.readyState&&200===n.status){var s=JSON.parse(n.responseText);e.innerHTML="";for(var i in s.potions){for(var t=s.potions[i],o=0;o<t.length;o++){var a=s.potions[i].ingredients[o];console.log(a)}e.innerHTML+='<div class="potions-thumb"><img src="img/thumbs/'+t.image+'"><p>'+t.name+' - <strong class="price">$'+t.price+'</strong></p></div><div class="potions-modal"><div class="potions-photo"><img src="img/'+t.image+'"></div><div class="potions-infos"><h2>'+t.name+"</h2><h2>Use/Effect</h2><p>"+t.effect+'</p><h2>Ingredients:</h2><ul></ul><h2>Price:</h2><h2 class="price">$ '+t.price+"</h2></div></div>"}}},n.send(null),e.innerHTML="requesting..."}